/* Default styles */
.container {
    display: flex;
    justify-content: center;
    align-items: center;
    height: 100vh;
  }

  .chat-window {
    width: 500px;
    height: 600px;
    border: 2px dashed #ccc;
    border-radius: 10px;
    overflow: hidden;
    display: flex;
    flex-direction: column;
    padding: 10px;
  }

  .chat-body {
    flex: 1;
    padding: 10px;
    overflow-y: hidden;
  }

  .message-list {
    list-style-type: none;
    padding: 0;
    margin: 0;
  }

  .message {
    margin-bottom: 10px;
    padding: 10px;
    border-radius: 10px;
    max-width: 80%;
    color: #fff;
  }

  .message.received {
    background-color: #a6bddb;
    align-self: flex-start;
    list-style-type: none; /* Add this line to remove bullet points */
  }


  .message.sent {
    background-color: #3c7b8e;
    align-self: flex-end;
    width: fit-content;
    padding: 10px;
    margin-left: auto;
    margin-right: 10px;
    border-radius: 10px;
    box-sizing: border-box;
  }

  .message-sender {
    font-weight: bold;
    margin-right: 10px;
  }

  .chat-footer {
    display: flex;
    align-items: center;
    padding: 10px;
  }

  .message-input {
    flex: 1;
    padding: 10px;
    border: 1px solid #ccc;
    border-radius: 5px;
    width: 100%;
    box-sizing: border-box;
  }

  .send-button {
    margin-left: 10px;
    padding: 10px;
    border: none;
    border-radius: 5px;
    background-color: #3c7b8e;
    color: #fff;
    font-weight: bold;
  }

  /* Mobile styles */
  @media only screen and (max-width: 600px) {
    .container {
      padding: 10px;
    }

    .chat-window {
      width: 100%;
      height: 100%;
      border: none;
      border-radius: 0;
    }

    .chat-body {
      padding: 0;
    }

    .message-list {
      padding: 10px;
    }

    .chat-footer {
      padding: 0;
      flex-direction: column;
    }

    .message-input {
      flex: 1;
      margin-top: 10px;
      margin-bottom: 10px;
      border-radius: 0;
      width: 100%;
    }

    .send-button {
      margin-left: 0;
      margin-top: 10px;
      border-radius: 0;
      width: 100%;
    }
  }

