/* Default styles */
.container {
  /* display: flex;
  justify-content: center;
  align-items: center; */
  /* height: 80vh; */
  display: block;
  position: relative;
  padding: 2px;
  margin: 0;
  border-radius: 10px;
  /* border: 3px solid rgba(0, 0, 0, 0); */
  overflow: hidden;
}
.container .info-bar {
  display: none;
  position: absolute;
  width: 100%;
  bottom: 0;
  left: 0;
  color: #eee;
  padding: 2px 8px 2px 8px;
  background-color: rgba(0, 0, 0, 0.35);
  grid-template-columns: minmax(50px, auto) 1fr minmax(50px, auto);
  z-index: 5;
  height: 50px;
  align-items: center;
}

.container:hover {
  .info-bar {
    display: grid;
  }
}

.container video {
  position: absolute;
  left: 0;
  top: 0;
  width: 100%;
  height: 100%;
  background-color: #aaa;
  object-fit: cover;
  border-radius: 10px;
}
.video-window {
  width: 1280px;
  height: 650px;
  border: 2px dashed #ccc;
  border-radius: 10px;
  overflow: hidden;
  display: flex;
  flex-direction: column;
  padding: 10px;
  background-color: #aaa;
}

/* Mobile styles */
@media only screen and (max-width: 600px) {
  .container {
    padding: 10px;
  }

  .chat-window {
    width: 100%;
    height: 100%;
    border: none;
    border-radius: 0;
  }

  .chat-body {
    padding: 0;
  }

  .message-list {
    padding: 10px;
  }

  .chat-footer {
    padding: 0;
    flex-direction: column;
  }

  .message-input {
    flex: 1;
    margin-top: 10px;
    margin-bottom: 10px;
    border-radius: 0;
    width: 100%;
  }

  .send-button {
    margin-left: 0;
    margin-top: 10px;
    border-radius: 0;
    width: 100%;
  }
}

figure {
  max-width: 64rem;
  width: 100%;
  max-height: 30.875rem;
  height: 100%;
  margin: 1.25rem auto;
  padding: 1.051%;
  background-color: #666;
}
.controls {
  width: 100%;
  height: 8.0971659919028340080971659919028%; /* of figure's height */
  position: relative;
}
.controls[data-state="hidden"] {
  display: none;
}

.controls[data-state="visible"] {
  display: block;
}
.controls > * {
  float: left;
  width: 3.90625%;
  height: 100%;
  margin-left: 0.1953125%;
  display: block;
}

.controls > *:first-child {
  margin-left: 0;
}
.controls .progress {
  cursor: pointer;
  width: 75.390625%;
}
.controls button {
  border: none;
  cursor: pointer;
  background: transparent;
  background-size: contain;
  background-repeat: no-repeat;
}
.controls button:hover,
.controls button:focus {
  opacity: 0.5;
}
.controls button[data-state="play"] {
  background-image: url("data:image/png;base64,iVBORw0KGgoAAA…");
}

.controls button[data-state="pause"] {
  background-image: url("data:image/png;base64,iVBORw0KGgoAAA…");
}
.controls progress {
  display: block;
  width: 100%;
  height: 81%;
  margin-top: 0.125rem;
  border: none;
  color: #0095dd;
  -moz-border-radius: 2px;
  -webkit-border-radius: 2px;
  border-radius: 2px;
}
.controls progress[data-state="fake"] {
  background: #e6e6e6;
  height: 65%;
}
.controls progress span {
  width: 0%;
  height: 100%;
  display: inline-block;
  background-color: #2a84cd;
}
.controls progress::-moz-progress-bar {
  background-color: #0095dd;
}

.controls progress::-webkit-progress-value {
  background-color: #0095dd;
}

